{
    //查询
    "search": {
        //模糊查询
        "like": "t", // name like '%t%' OR description like '%t%',
        "like": "t%", // name like 't%' OR description like 't%'
        "like": "%t", // name like '%t' OR description like '%t'
        "like": "%' and id=1 and email like 't2", // sql注入(结果为空)
        //精确查询
        "name": "T1", // name == T1
        "name": {
            "==": "" //name=‘’，默认会过滤掉空字符串“ ”
        },
        "email": "t1@focus-ui.com", // email == t1@focus-ui.com
        "email": { // email like '%t%'
            "like": "t1"
        },
        //比较&条件
        "updated_at": { // updated_at >= 2022-01-01 00:00:00" AND updated_at <  2023-01-01 00:00:00
            ">=": "2022-01-01 00:00:00",
            "<": "2023-01-01 00:00:00"
        },
        "age": { // age BETWEEN 10 AND 20
            "between": [
                10,
                20
            ]
        },
        "city": { // city IN ('Paris','London')
            "in": [
                "Paris",
                "London"
            ]
        },
        "address.city": "Paris", // relation查询
        "address": { // relation查询
            "like": "Paris",
            "city": "Paris"
        },
        // 关键字
        // 1. 在不冲突的情况下，可以省略开头的‘_’ ex)ignore_null/ors/like，
        // 2. 如果两者都存在，优先使用以'_'开头的属性 ex)_like > like
        "_ignore_null": false, // 是否忽略空值(null/'')，默认为True(忽略)
        "_str_sep": "||", // 字符串分隔符，默认为 "||"，ex) "_ors": {"email": "t1@focus-ui.com||t2@flaskz.com"}会被拆分成email==t1@focus-ui.com OR  email = t2@flaskz.com

        "_like": "t", // 模糊查询关键字==like，like字段冲突时使用，优先级_like>_like
        "_ilike": "t", // 不区分大小写模糊查询关键字==ilike(需DB支持)，ilike字段冲突时使用，优先级_ilike>ilike
        "_likejoin": "or", //  模糊查询(包含)的连接方式，可选值为'or'/'and'，默认为or(任意一个字段包含即符合)
        
        "notlike": "t", // 不包含模糊查询, name notlike '%t%' AND description notlike '%t%'
        "_notlike": "t", // 不包含模糊查询关键字==notlike，notlike字段冲突时使用，优先级_notlike>_notlike
        "_notilike": "t", // 不区分大小写不包含模糊查询关键字==notilike(需DB支持)，notilike字段冲突时使用，优先级_notilike>notilike
        "_notilikejoin": "and", // 模糊查询(不包含)的连接方式，可选值为'or'/'and'，默认为and(所有字段都不包含)

        "_like_columns": [ "name" ], // 模糊查询的列，默认使用模型类的like_columns
        
        "_ors": { // 通过OR进行连接的多个查询条件集合
            "email": "t1@focus-ui.com||t2@flaskz.com", //  email = t1@focus-ui.com OR email = t2@flaskz.com
            "name": {
                "in": [
                    "t1",
                    "t2"
                ]
            },
            "age": {
                "between": [
                    10,
                    20
                ]
            }
        },
        "_ands": { // 通过AND进行连接的多个查询条件集合
            "age": { // age >10 AND age<20
                ">": 10,
                "<": 20
            }
        }
    },
    //排序
    "sort": "name", // ORDER BY name ASC
    "sort": { // ORDER BY name DESC
        "field": "name",
        "order": "desc"
    },
    "sort": [ // ORDER BY name ASC, age DESC
        {
            "field": "name",
            "order": "asc" // asc/desc
        },
        {
            "field": "age",
            "order": "desc" // asc/desc
        }
    ],
    //分页
    "page": { // LIMIT ? OFFSET ? (20, 0)
        "offset": 0,
        "size": 20
    },
    //分组
    "group": "email" //GROUP BY email
}

